## Check Sender Address balance ##
balance = web3.eth.get_balance(sender_address)
balance_converted = web3.fromWei(balance,'ether')
print("Wallet balance:" , balance_converted,"ETH")
####

contract  = web3.eth.contract(address=PCSRouter_address , abi=panabi)
## Setting token want to swap
tokenToBuy = web3.toChecksumAddress(input("Enter Token Address: "))

# WETH Address (wrapped Ether)
spend   = Web3.toChecksumAddress("0x55d398326f99059fF775485246999027B3197955")  # Địa chỉ của WETH

## Definition ##
def checkToken(_token_CA):
    # Cung cấp phần định nghĩa hàm checkToken cho mã của bạn
    pass

if CheckTax == True :
    try:
        TokenInfo = checkToken(tokenToBuy)
    except:
        print("Please check Token CA again. Maybe not listed yet or rugged!")
        raise SystemExit
    else:
        if TokenInfo[2] == True:
            print("Token is HoneyPot, quitting.")
            raise SystemExit
        if TokenInfo[0] > MaxBuyTax:
            print("Current Buy Tax: " + str(TokenInfo[0]) +"%, exceeded your setting: "+ str(MaxBuyTax) +"% --> Stop swapping.")
            raise SystemExit
        if TokenInfo[1] > MaxSellTax:
            print("Token Sell Tax: " + str(TokenInfo[1]) +"%, exceeded your setting: "+ str(MaxSellTax) +"% --> Stop swapping.")
            raise SystemExit

## Check total transaction of sender Wallet ##
nonce = web3.eth.get_transaction_count(sender_address)
count = 1
while count <= MaxTx:
   uniswap_txn = contract.functions.swapExactETHForTokens(
       0,
       # set to 0 means dont care slippage, or specify minimum amount of token you want to receive - consider Decimal!
       [spend, tokenToBuy],
       sender_address,
       (int(time.time()) + 10000)
   ).buildTransaction({
       'from': sender_address,
       'value': web3.toWei(ETHAmount, 'ether'),  ## ETH Amount want to Swap from
       'gas': GasLimit,
       'gasPrice': web3.toWei(Gwei, 'gwei'),
       'nonce': nonce,
   })
   signed_txn = web3.eth.account.sign_transaction(uniswap_txn, private_key=sender_private_key)
   tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
   print("TX Hash: https://etherscan.io/tx/" + tx_token.hex())
   nonce = nonce + 1
   count = count + 1

print(datetime.datetime.now().time(), "Waiting for last transaction to be done")
web3.eth.wait_for_transaction_receipt(tx_token)
print(datetime.datetime.now().time(), "Transactions Finished!")
print("Block finish transaction: ", web3.eth.blockNumber)
